package com.sonata;

public class Arithmetic_Case {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 12, b = 5;

	    
	    System.out.println("a + b = " + (a + b));

	    
	    System.out.println("a - b = " + (a - b));

	    
	    System.out.println("a * b = " + (a * b));

	    
	    System.out.println("a / b = " + (a / b));

	    
	    System.out.println("a % b = " + (a % b));
	}

}
